import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Message } from 'src/app/models/message';
import { Person } from 'src/app/models/person';
import { Subject } from 'src/app/models/subject';
import { PersonServices } from 'src/app/services/person.service';
import { SubjectService } from 'src/app/services/subject.service';
import { MessageService } from 'src/app/services/message.service';

@Component({
  selector: 'app-first',
  templateUrl: './first.component.html',
  styleUrls: ['./first.component.css']
})
export class FirstComponent {
  @Input() messageForm: Message = new Message;
  @Input() personForm: Person = new Person;


  form: FormGroup = new FormGroup({
    email: new FormControl('', [
      Validators.email,
      Validators.required
    ]),
    name: new FormControl(null, [
      Validators.required,
      Validators.minLength(2)
    ]),
    phone: new FormControl('', [
      Validators.required
    ]),
    message: new FormControl('', [
      Validators.required
    ]),
    subjectId: new FormControl('', [
      Validators.required
    ]),
    recaptcha: new FormControl('', [
      Validators.required
    ]),

  });
  title = 'SofTrust.UI';

  subjects: Subject[] = []
  message: Message = new Message
  person: Person = new Person

  siteKey = '6Ldkq-MkAAAAAG16wm76dnES4mCo-VQRe-eebM1Z'
  isCaptcha: boolean = true;

  constructor(
    private subjectServices: SubjectService,
    private messageService: MessageService,
    private formBuilder: FormBuilder,
    private personServices : PersonServices,
    private router: Router,
    ) {}

  ngOnInit(): void {
    this.subjectServices
    .getSubjects()
    .subscribe((result: Subject[]) => (this.subjects = result));

    console.log(this.subjects);
  }

  createMessage(){
    this.personServices
    .createPerson(this.personForm, this.messageForm.subjectId, this.messageForm.message)
    .subscribe({
      next:(result: Person) => {this.person = result}
    });

    //this.goToNextPage();
  }

  async goToNextPage() {
    await this.router.navigate(['/second']);
  }
}
<div class="container">
  <div class="cont">
    <h1>Напишите Нам</h1>
    <div class="content">


    <div class="left">
      <label class="label_item">Ваше имя </label>
      <label class="label_item">Email </label>
      <label class="label_item">Телефон </label>
      <label class="label_item">Тема </label>
      <label class="label_item">Сообщение </label>
    </div>

    <div class="right">

      <form class="card" [formGroup]="form">


        <div class="form-control">

          <input type="text" [(ngModel)]="personForm.name"  placeholder="Имя" formControlName="name">
        </div>
          <div
            *ngIf="form.get('name')?.invalid && form.get('name')?.touched"
            class="validation">

            <small *ngIf="form.get('name')?.hasError('required')">
              Поле Имя не может быть пустым
            </small>

            <small *ngIf="form.get('name')?.errors?.['minlength']" >
              Длина дожна быть не менее 2 символа
            </small>

          </div>


        <br>

        <div class="form-control">

          <input type="text" [(ngModel)]="personForm.email" placeholder="Email" formControlName="email">
          <div
            *ngIf="form.get('email')?.invalid && form.get('email')?.touched"
            class="validation">


            <small *ngIf="form.get('email')?.hasError('required')">
              Поле email не может быть пустым
            </small>
            <small *ngIf="form.get('email')?.hasError('email')">
              Некорретный email
            </small>
          </div>
        </div>

        <br>

        <div class="form-control">

          <p-inputMask styleClass="form-control" [(ngModel)]="personForm.phone" placeholder="8 (999) 999-99-99" formControlName="phone" mask="8 (999) 999-99-99"></p-inputMask>

          <div
            *ngIf="form.get('phone')?.invalid && form.get('phone')?.touched"
            class="validation">

            <small *ngIf="form.get('phone')?.hasError('required')">
              Поле Телефон не может быть пустым
            </small>
          </div>
        </div>

        <br>

        <div class="form-control">

          <select [(ngModel)]="messageForm.subjectId"  formControlName="subjectId">
            <option
              *ngFor="let subject of subjects"
              value="{{subject.id}}"
              [ngValue]="subject.id"
            >
              {{subject.name}}
            </option>
          </select>
        </div>

        <br>

        <div class="form-control">

          <textarea  type="text" [(ngModel)]="messageForm.message" placeholder="Сообщение" formControlName="message" ></textarea>
          <div
            *ngIf="form.get('message')?.invalid && form.get('message')?.touched"
            class="validation">


            <small *ngIf="form.get('message')?.hasError('required')">
              Поле Сообщение не может быть пустым
            </small>

          </div>
        </div>

        <br>

        <div class="form-control">
        <ngx-recaptcha2 formControlName="message" #captchaElem
          [siteKey] = "siteKey"
          formControlName="recaptcha">
        </ngx-recaptcha2>
      </div>

        <br>

        <button (click)="createMessage()" id="hider" [disabled]="form.invalid" >Отправить</button>

      </form>
    </div>
    </div>
  </div>
</div>
