  styleUrls: ['./second.component.css']
})
export class SecondComponent {

  constructor(
    ) {}

  ngOnInit(): void {

  }
}
<div class="form">
  <h2 class="mainText">Напишите нам</h2>
  <div class="formInput">
    <span><i class="fas fa-user"></i></span>
    <input type="text" placeholder="Введите ваше имя">
  </div>
  <div class="formInput">
